import { Module } from "@medusajs/framework/utils"
import TestModuleService from "./service"

export const TEST_MODULE = "test"

export default Module(TEST_MODULE, {
  service: TestModuleService,
})

import { MedusaService } from "@medusajs/framework/utils"
import Test from "./models/test"

class TestModuleService extends MedusaService({
  Test,
}) {}

export default TestModuleService

import { model } from "@medusajs/framework/utils"

const Test = model.define("test", {
  id: model.id().primaryKey(),
  name: model.text(),
})

export default Test

import type { MedusaRequest, MedusaResponse } from "@medusajs/framework/http";

export const POST = async (req: MedusaRequest, res: MedusaResponse) => {
    res.json({
        test: true,
        test2: false,
    })
}
