import { createStep, StepResponse } from "@medusajs/framework/workflows-sdk"
import { ContainerRegistrationKeys } from "@medusajs/framework/utils"
import { Query } from "@medusajs/framework/modules-sdk"

type GetSellerProductsStepInput = {
  seller_id: string
}

export const getSellerProductsStep = createStep(
  "get-seller-products",
  async (input: GetSellerProductsStepInput, { container }) => {
    const query = container.resolve<Query>(ContainerRegistrationKeys.QUERY)

    const { data: relations } = await query.graph({
      entity: "product_seller",
      fields: [
        "product.*",
        "product.variants.*",
        "product.variants.prices.*",
        "product.variants.options.*",
        "product.options.*",
        "product.collection.*",
        "product.categories.*",
        "product.sales_channels.*",
        "product.images.*",
        "product.tags.*",
        "product.type.*",
      ],
      filters: {
        seller_id: input.seller_id,
      },
    })

    const products = relations.map((r: any) => r.product)

    return new StepResponse(products)
  }
)

import {
  createWorkflow,
  transform,
  WorkflowResponse,
} from "@medusajs/framework/workflows-sdk"
import { parseProductCsvStep } from "@medusajs/core-flows"
import { createProductsWorkflow } from "@medusajs/core-flows"
import { validateProductsToImportStep } from "./steps/validate-products-to-import"

type ImportSellerProductsWorkflowInput = {
  file_content: string
  seller_id: string
}

export const importSellerProductsWorkflow = createWorkflow(
  "import-seller-products",
  function (input: ImportSellerProductsWorkflowInput) {
    const parsedProducts = parseProductCsvStep(input.file_content)

    validateProductsToImportStep({ products: parsedProducts })

    const productsInput = transform(
      { parsedProducts },
      ({ parsedProducts }) => ({
        products: parsedProducts.map((p: any) => ({
          ...p,
          status: p.status || "draft",
        })),
      })
    )

    const createdProducts = createProductsWorkflow.runAsStep({
      input: productsInput,
    })

    return new WorkflowResponse(createdProducts)
  }
)

import {
  createWorkflow,
  transform,
  WorkflowResponse,
} from "@medusajs/framework/workflows-sdk"
import {
  generateProductCsvStep,
  useQueryGraphStep,
} from "@medusajs/core-flows"
import { getSellerProductsStep } from "./steps/get-seller-products"

type ExportSellerProductsWorkflowInput = {
  seller_id: string
}

export const exportSellerProductsWorkflow = createWorkflow(
  "export-seller-products",
  function (input: ExportSellerProductsWorkflowInput) {
    const products = getSellerProductsStep({
      seller_id: input.seller_id,
    })

    const csvFile = generateProductCsvStep(products)

    const fileData = useQueryGraphStep({
      entity: "file",
      fields: ["id", "url"],
      filters: {
        id: csvFile.id,
      },
      options: {
        throwIfKeyNotFound: true,
      },
    })

    const result = transform({ fileData }, ({ fileData }) => {
      const file = fileData.data[0]
      return { url: file.url }
    })

    return new WorkflowResponse(result)
  }
)

import {
  AuthenticatedMedusaRequest,
  MedusaResponse,
} from "@medusajs/framework/http"
import { MedusaError } from "@medusajs/framework/utils"
import { importSellerProductsWorkflow } from "../../../../workflows/import-seller-products"
import { fetchSellerByAuthActorId } from "../helpers/helpers"

export const POST = async (
  req: AuthenticatedMedusaRequest<{ file: Express.Multer.File }>,
  res: MedusaResponse<{ summary: { created: number } }>
) => {
  const file = (req as any).file as Express.Multer.File | undefined

  if (!file) {
    throw new MedusaError(
      MedusaError.Types.INVALID_DATA,
      "No file uploaded. Please upload a CSV file."
    )
  }

  const seller = await fetchSellerByAuthActorId(
    req.auth_context.actor_id,
    req.scope
  )

  if (!seller) {
    throw new MedusaError(
      MedusaError.Types.NOT_FOUND,
      "Seller not found for the current user."
    )
  }

  const fileContent = file.buffer.toString("utf-8")

  const { result } = await importSellerProductsWorkflow(req.scope).run({
    input: {
      file_content: fileContent,
      seller_id: seller.id,
    },
  })

  res.status(200).json({
    summary: {
      created: Array.isArray(result) ? result.length : 0,
    },
  })
}

import {
  AuthenticatedMedusaRequest,
  MedusaResponse,
} from "@medusajs/framework/http"
import { MedusaError } from "@medusajs/framework/utils"
import { exportSellerProductsWorkflow } from "../../../../workflows/export-seller-products"
import { fetchSellerByAuthActorId } from "../helpers/helpers"

export const POST = async (
  req: AuthenticatedMedusaRequest,
  res: MedusaResponse<{ url: string }>
) => {
  const seller = await fetchSellerByAuthActorId(
    req.auth_context.actor_id,
    req.scope
  )

  if (!seller) {
    throw new MedusaError(
      MedusaError.Types.NOT_FOUND,
      "Seller not found for the current user."
    )
  }

  const { result } = await exportSellerProductsWorkflow(req.scope).run({
    input: {
      seller_id: seller.id,
    },
  })

  res.json(result)
}

import { ContainerRegistrationKeys } from "@medusajs/framework/utils"
import { MedusaContainer } from "@medusajs/framework/types"

export const fetchSellerByAuthActorId = async (
  authActorId: string,
  scope: MedusaContainer,
  fields: string[] = ["id"]
) => {
  const query = scope.resolve(ContainerRegistrationKeys.QUERY)

  const {
    data: [seller],
  } = await query.graph({
    entity: "seller",
    filters: { id: authActorId },
    fields,
  })

  return seller
}

import { MiddlewareRoute } from "@medusajs/medusa"
import multer from "multer"

const upload = multer({ storage: multer.memoryStorage() })

export const productImportExportMiddlewares: MiddlewareRoute[] = [
  {
    method: ["POST"],
    matcher: "/vendor/products/import",
    middlewares: [upload.single("file")],
  },
]

import { useMutation, useQueryClient } from "@tanstack/react-query"
import type { UseMutationOptions } from "@tanstack/react-query"
import { queryKeysFactory } from "@mercurjs/dashboard-shared"
import { ClientError } from "@mercurjs/client"
import { client } from "../../lib/client"

const PRODUCTS_QUERY_KEY = "vendor_products" as const
export const productsQueryKeys = queryKeysFactory(PRODUCTS_QUERY_KEY)

type ImportProductsResponse = { summary: { created: number } }
type ExportProductsResponse = { url: string }

export const useImportProducts = (
  options?: UseMutationOptions<ImportProductsResponse, ClientError, File>
) => {
  const queryClient = useQueryClient()

  return useMutation({
    mutationFn: async (file: any) => client.vendor.products.import.mutate({file, fetchOptions: {headers:{'Content-Type': 'multipart/form-data'}}}),
    onSuccess: (data, variables, context) => {
      queryClient.invalidateQueries({ queryKey: productsQueryKeys.lists() })
      options?.onSuccess?.(data, variables, context)
    },
    ...options,
  })
}

export const useExportProducts = (
  options?: UseMutationOptions<ExportProductsResponse, ClientError, void>
) => {
  return useMutation({
    mutationFn: async () =>
      client.vendor.products.export.mutate(),
    onSuccess: (data, variables, context) => {
      options?.onSuccess?.(data, variables, context)
    },
    ...options,
  })
}

import { useState } from "react";
import { Button, Heading } from "@medusajs/ui";

import { RouteDrawer } from "@mercurjs/dashboard-shared";
import { UploadImport } from "./components/upload-import";
import { ImportSummary } from "./components/import-summary";

export default function ProductImportPage() {
  const [summary, setSummary] = useState<{ created: number } | null>(null);

  return (
    <RouteDrawer>
      <RouteDrawer.Header>
        <RouteDrawer.Title asChild>
          <Heading>Import Products</Heading>
        </RouteDrawer.Title>
        <RouteDrawer.Description>
          Upload a CSV file to import products into your store.
        </RouteDrawer.Description>
      </RouteDrawer.Header>
      <RouteDrawer.Body>
        {summary ? (
          <ImportSummary created={summary.created} />
        ) : (
          <UploadImport onSuccess={setSummary} />
        )}
      </RouteDrawer.Body>
      <RouteDrawer.Footer>
        <RouteDrawer.Close asChild>
          <Button variant="secondary">{summary ? "Done" : "Cancel"}</Button>
        </RouteDrawer.Close>
      </RouteDrawer.Footer>
    </RouteDrawer>
  );
}

import { useState } from "react";
import { Button, toast } from "@medusajs/ui";
import { Trash } from "@medusajs/icons";

import { FileUpload, FilePreview } from "@mercurjs/dashboard-shared";
import type { FileType } from "@mercurjs/dashboard-shared";
import { useImportProducts } from "../../../../hooks/api/product-import-export";
import { downloadImportTemplate } from "../helpers/import-template";

type UploadImportProps = {
  onSuccess: (summary: { created: number }) => void;
};

export const UploadImport = ({ onSuccess }: UploadImportProps) => {
  const [file, setFile] = useState<FileType | null>(null);

  const { mutateAsync: importProducts, isPending } = useImportProducts({
    onSuccess: (data) => {
      toast.success(
        `Successfully imported ${data.summary?.created ?? 0} product(s).`,
      );
      onSuccess(data.summary);
    },
    onError: (error) => {
      toast.error(error.message);
    },
  });

  const handleFileUpload = (files: FileType[]) => {
    if (files.length > 0) {
      setFile(files[0] ?? null);
    }
  };

  const handleImport = async () => {
    if (!file) return;
    await importProducts(file.file);
  };

  const handleRemoveFile = () => {
    setFile(null);
  };

  return (
    <div className="flex flex-col gap-y-4">
      <div className="rounded-lg border border-ui-border-base bg-ui-bg-subtle p-3">
        <span className="text-ui-fg-subtle txt-small">
          Upload a CSV file with your products. You can{" "}
          <button
            type="button"
            className="text-ui-fg-interactive hover:text-ui-fg-interactive-hover underline"
            onClick={downloadImportTemplate}
          >
            download the template
          </button>{" "}
          to get started.
        </span>
      </div>

      {!file ? (
        <FileUpload
          label="Upload CSV file"
          hint="Only .csv files are supported"
          formats={[".csv", "text/csv"]}
          onUploaded={handleFileUpload}
          multiple={false}
        />
      ) : (
        <FilePreview
          filename={file.file.name}
          loading={isPending}
          activity="Importing products..."
          actions={[
            {
              actions: [
                {
                  icon: <Trash />,
                  label: "Remove",
                  onClick: handleRemoveFile,
                },
              ],
            },
          ]}
        />
      )}

      {file && (
        <div className="flex justify-end">
          <Button onClick={handleImport} isLoading={isPending} disabled={!file}>
            Import Products
          </Button>
        </div>
      )}
    </div>
  );
};

import { Text } from "@medusajs/ui"
import { CheckCircleSolid } from "@medusajs/icons"

type ImportSummaryProps = {
  created: number
}

export const ImportSummary = ({ created }: ImportSummaryProps) => {
  return (
    <div className="flex flex-col items-center gap-y-4 py-8">
      <CheckCircleSolid className="text-ui-fg-interactive" />
      <div className="flex flex-col items-center gap-y-1">
        <Text size="large" weight="plus">
          Import Complete
        </Text>
        <Text className="text-ui-fg-subtle">
          {created} product(s) were successfully created.
        </Text>
      </div>
    </div>
  )
}

import { Button, Heading, Text, toast } from "@medusajs/ui"

import { RouteDrawer, useRouteModal } from "@mercurjs/dashboard-shared"
import { useExportProducts } from "../../../hooks/api/product-import-export"

function ExportProductsContent() {
  const { handleSuccess } = useRouteModal()

  const { mutateAsync: exportProducts, isPending } = useExportProducts({
    onSuccess: (data) => {
      if (data.url) {
        const a = document.createElement("a")
        a.href = data.url
        a.download = `products-export-${Date.now()}.csv`
        a.click()
      }
      toast.success("Products exported successfully.")
      handleSuccess()
    },
    onError: (error) => {
      toast.error(error.message)
    },
  })

  const handleExport = async () => {
    await exportProducts()
  }

  return (
    <>
      <RouteDrawer.Header>
        <RouteDrawer.Title asChild>
          <Heading>Export Products</Heading>
        </RouteDrawer.Title>
        <RouteDrawer.Description>
          Export your products as a CSV file.
        </RouteDrawer.Description>
      </RouteDrawer.Header>
      <RouteDrawer.Body>
        <div className="flex flex-col gap-y-4">
          <Text className="text-ui-fg-subtle">
            This will export all your products to a CSV file. The download will
            start automatically when the export is ready.
          </Text>
        </div>
      </RouteDrawer.Body>
      <RouteDrawer.Footer>
        <RouteDrawer.Close asChild>
          <Button variant="secondary">Cancel</Button>
        </RouteDrawer.Close>
        <Button onClick={handleExport} isLoading={isPending}>
          Export
        </Button>
      </RouteDrawer.Footer>
    </>
  )
}

export default function ProductExportPage() {
  return (
    <RouteDrawer>
      <ExportProductsContent />
    </RouteDrawer>
  )
}
