} from "@medusajs/framework/utils"

import sellerMember from "../../../../links/seller-member"

export const fetchSellerByAuthActorId = async (
  authActorId: string,
  scope: MedusaContainer,
  fields: string[] = ["id"]
) => {
  const query = scope.resolve(ContainerRegistrationKeys.QUERY)

  const {
    data: [seller],
  } = await query.graph({
    entity: sellerMember.entryPoint,
    filters: { member_id: authActorId },
    fields: fields.map((f) => `seller.${f}`),
  })

  return seller?.seller
}

export const filterBySellerId = () => {
  return async (
    req: AuthenticatedMedusaRequest,
    _res: MedusaResponse,
    next: MedusaNextFunction
  ) => {
    const seller = await fetchSellerByAuthActorId(
      req.auth_context.actor_id,
      req.scope
    )

    if (!seller) {
      throw new MedusaError(
        MedusaError.Types.NOT_FOUND,
        "Seller not found for the current user"
      )
    }

    req.filterableFields.seller_id = seller.id

    return next()
  }
}

export const checkResourceOwnershipByResourceId = ({
  entryPoint,
}: {
  entryPoint: string
}) => {
  return async (
    req: AuthenticatedMedusaRequest,
    _res: MedusaResponse,
    next: MedusaNextFunction
  ) => {
    const query = req.scope.resolve(ContainerRegistrationKeys.QUERY)

    const seller = await fetchSellerByAuthActorId(
      req.auth_context.actor_id,
      req.scope
    )

    if (!seller) {
      throw new MedusaError(
        MedusaError.Types.NOT_FOUND,
        "Seller not found for the current user"
      )
    }

    const {
      data: [resource],
    } = await query.graph({
      entity: entryPoint,
      filters: { id: req.params.id },
      fields: ["seller_id"],
    })

    if (!resource || resource.seller_id !== seller.id) {
      throw new MedusaError(
        MedusaError.Types.NOT_FOUND,
        `Resource with id: ${req.params.id} was not found`
      )
    }

    return next()
  }
}

