import { AuthenticatedMedusaRequest, MedusaResponse } from "@medusajs/framework"
import { ContainerRegistrationKeys } from "@medusajs/framework/utils"
import { fetchSellerByAuthActorId } from "./helpers/helpers"

export async function GET(
  req: AuthenticatedMedusaRequest,
  res: MedusaResponse
): Promise<void> {
  const query = req.scope.resolve(ContainerRegistrationKeys.QUERY)
  const seller = await fetchSellerByAuthActorId(
    req.auth_context.actor_id,
    req.scope
  )

  const { data: notifications, metadata } = await query.graph({
    entity: "notification",
    fields: req.queryConfig.fields,
    filters: { channel: "seller_feed", to: seller.id },
    pagination: req.queryConfig.pagination,
  })

  res.json({
    notifications,
    count: metadata?.count,
    offset: metadata?.skip,
    limit: metadata?.take,
  })
}

import { validateAndTransformQuery } from "@medusajs/framework"
import { MiddlewareRoute } from "@medusajs/medusa"
import { vendorNotificationQueryConfig } from "./query-config"
import { VendorGetNotificationParams } from "./validators"

export const vendorNotificationMiddlewares: MiddlewareRoute[] = [
  {
    method: ["GET"],
    matcher: "/vendor/notifications",
    middlewares: [
      validateAndTransformQuery(
        VendorGetNotificationParams,
        vendorNotificationQueryConfig.list
      ),
    ],
  },
]

export const defaultVendorNotificationFields = [
  "id",
  "to",
  "channel",
  "template",
  "data",
  "created_at",
  "updated_at",
]

export const vendorNotificationQueryConfig = {
  list: {
    defaults: defaultVendorNotificationFields,
    isList: true,
  },
  retrieve: {
    defaults: defaultVendorNotificationFields,
    isList: false,
  },
}

import { ContainerRegistrationKeys } from "@medusajs/framework/utils"
import { MedusaContainer } from "@medusajs/framework/types"

export const fetchSellerByAuthActorId = async (
  authActorId: string,
  scope: MedusaContainer,
  fields: string[] = ["id"]
) => {
  const query = scope.resolve(ContainerRegistrationKeys.QUERY)

  const {
    data: [seller],
  } = await query.graph({
    entity: "seller",
    filters: { id: authActorId },
    fields,
  })

  return seller
}

import { Notifications } from "@mercurjs/vendor"

export default function TopbarActions() {
  return <Notifications />
}
